<script>
import { Encrypt } from '@/utils/validate.js';
import Version from '@/components/version.vue';
import Support from '@/components/support.vue';
import { reload } from "@/utils/util.js";
import { setReadStatus, postLogs } from "@/api/base.js";
// import { mixins } from "@/utils/mixins.js";
export default {
  name: 'Auth',
  components: { Version, Support },
  // mixins: [mixins],
  data() {
    return {
        siteTitle: process.env.VUE_APP_WEB_TITLE,
        isAgree: true,
        isUnRead: true,  // 是否已读通知公告
        dotshow: true, // 显示加载点
        message1: '正在获取微信授权',
        message2: '请耐心等待',
        message3: '使用别人分享给您的地址或二维码是无法正常使用应用的，请到官方指定的入口进入应用，如有疑问可联系相关人员。',
        mdata:''
    }
  },
  computed: {
    openid() {
        return this.$store.state.app.openid;
    },
    token() {
        return this.$store.state.app.token;
    },
    userInfo(){
      return this.$store.state.app.userinfo;
    }
  },
  mounted() {
    this.getAuth();
  },
  activated(){
  },
  methods: {
     getAuth(){
         this.dotshow=true;
         this.message1='正在获取微信授权';
         this.message2= '请耐心等待';
          // 微信
          this.message3=process.env.VUE_APP_KEY_AUTH_WX_TXT;
          this.$store.dispatch("app/getUserInfoByToken").then((resp) => {
              this.dotshow=false;
              if(resp || this.token){
                this.message1 = "授权成功！";
                this.message2 = '';
                  // 已存在已读,进入授权
                  this.nativationTo();
                  this.isUnRead = false;
              } else{
                let _message= "授权失败，请稍后访问！";
                this.message1= _message;
                this.message2="";
                this.mdata="";
                // 写日志，保存url地址
                this.writeLog();
              }
          }).catch(err => {
              this.writeLog();
          });
     },
     // 写日志
     writeLog(logs = ''){
          logs = logs + " and platform="+('微信')+" and " + location.href;
      //  if(this.hasYangXin){
         // Uid，OpenId，Time，Url，UrlParam，Ip，ClientType
          // postLogs({Url: location.href, UrlParam: logs, ClientType: (this.isWeixin ? '微信' : '支付宝'), Time: new Date().getTime()}).then(ret => {
            
          // }).catch( err => {

          // });
      // }
     },
    readMe(){
      this.isUnRead = true;
    },
     // 入口处理
    nativationTo(){
        if(this.userInfo){
            // 进入页面
            this.$router.push({
                path: '/index'
            });
        }else if(this.openid){
          // 存在openid说明已经获取到微信授权信，没有登录，进入登录页
          this.$router.push({ path: '/login' });

        }
    },
    // 重新授权
    reLoadAuth() {
      reload();
    },
    // 帮助手册
    toPrivacyPolicy(){
      this.$router.push({ path: '/privacypolicy'});
    }
  }
}
</script>
